import http from "../http-common";

class UploadFilesService {
    upload(file, onUploadProgress) {
        let formData = new FormData();
        for(let i = 0; i< file.length; i++) {
            formData.append('images', file[i]);
        }

        return http.post("/upload", formData, {
            headers: {
                "Content-Type": "multipart/form-data",
                "Authorization":"eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI1ZGNhMjE0ZmJlMzU2NDM4ZjdjZjM1NDYiLCJqdGkiOiI1ZGNhMjE0ZmJlMzU2NDM4ZjdjZjM1NDYiLCJleHAiOjE2MjQ1MTA3MDgsInJvbGUiOiJBRE1JTiIsIl9pZCI6IjVkY2EyMTRmYmUzNTY0MzhmN2NmMzU0NiIsIm5hbWUiOiJhZG1pbiIsImVtYWlsIjoiYWRtaW5AZ21haWwuY29tIn0.Q_85-0gVhe5_UMmCzBvZ0miZSZjcb1WIj-Ny45WpISs"
            },
            onUploadProgress,
        });
    }
}

export default new UploadFilesService();
